d_file','2017/04/J1MCC1NVS1AUX@8C-1.png'),(72,42,'_wp_attachment_metadata','a:5:{s:5:\"width\";i:836;s:6:\"height\";i:596;s:4:\"file\";s:30:\"2017/04/J1MCC1NVS1AUX@8C-1.png\";s:5:\"sizes\";a:4:{s:9:\"thumbnail\";a:4:{s:4:\"file\";s:30:\"J1MCC1NVS1AUX@8C-1-150x150.png\";s:5:\"width\";i:150;s:6:\"height\";i:150;s:9:\"mime-type\";s:9:\"image/png\";}s:6:\"medium\";a:4:{s:4:\"file\";s:30:\"J1MCC1NVS1AUX@8C-1-300x214.png\";s:5:\"width\";i:300;s:6:\"height\";i:214;s:9:\"mime-type\";s:9:\"image/png\";}s:12:\"medium_large\";a:4:{s:4:\"file\";s:30:\"J1MCC1NVS1AUX@8C-1-768x548.png\";s:5:\"width\";i:768;s:6:\"height\";i:548;s:9:\"mime-type\";s:9:\"image/png\";}s:14:\"post-thumbnail\";a:4:{s:4:\"file\";s:30:\"J1MCC1NVS1AUX@8C-1-825x510.png\";s:5:\"width\";i:825;s:6:\"height\";i:510;s:9:\"mime-type\";s:9:\"image/png\";}}s:10:\"image_meta\";a:12:{s:8:\"aperture\";s:1:\"0\";s:6:\"credit\";s:0:\"\";s:6:\"camera\";s:0:\"\";s:7:\"caption\";s:0:\"\";s:17:\"created_timestamp\";s:1:\"0\";s:9:\"copyright\";s:0:\"\";s:12:\"focal_length\";s:1:\"0\";s:3:\"iso\";s:1:\"0\";s:13:\"shutter_speed\";s:1:\"0\";s:5:\"title\";s:0:\"\";s:11:\"orientation\";s:1:\"0\";s:8:\"keywords\";a:0:{}}}'),(79,46,'_edit_last','1'),(80,46,'_edit_lock','1493979795:1'),(93,56,'_edit_last','1'),(94,56,'_edit_lock','1493979810:1'),(99,62,'_edit_last','1'),(100,62,'_edit_lock','1493979829:1'),(105,64,'_edit_last','1'),(106,64,'_edit_lock','1493979844:1'),(113,68,'_edit_last','1'),(114,68,'_edit_lock','1493979628:1'),(123,74,'_edit_last','1'),(124,74,'_edit_lock','1493979646:1'),(129,78,'_edit_last','1'),(130,78,'_edit_lock','1497577482:1'),(141,87,'_edit_last','1'),(142,87,'_edit_lock','1498021258:1'),(159,97,'_edit_last','1'),(160,97,'_edit_lock','1498875886:1'),(165,102,'_edit_last','1'),(166,102,'_edit_lock','1498875903:1'),(175,109,'_edit_last','1'),(176,109,'_edit_lock','1499135439:1'),(179,111,'_edit_last','1'),(180,111,'_edit_lock','1499303317:1'),(183,114,'_edit_last','1'),(184,114,'_edit_lock','1500446019:1'),(